public enum OpenNLPModel \{
 SentenceDetector\("en\-sent\.bin"\)\,
 PersonNameFinder\("en\-ner\-person\.bin"\)\;
 private final String modelFileName\;

 OpenNLPModel\(String modelFileName\) \{
 this\.modelFileName \= modelFileName\;
 \}

 public String getModelFileName\(\) \{
 return modelFileName\;
 \}

 public static String getModelFileName\(OpenNLPModel openNLPModel\)\{
 switch \(openNLPModel\)\{
 case PersonNameFinder\:
 return openNLPModel\.getModelFileName\(\)\;
 case SentenceDetector\:
 return openNLPModel\.getModelFileName\(\)\;
 default\:
 return null\;
 \}
 \}
\}

